import {
  Stack,
  Drawer,
  AppBar,
  Toolbar,
  IconButton,
  ListItemText,
  ListItemIcon,
  ListItemButton,
  ListItem,
  List,
  ListSubheader,
  Collapse,
  Avatar,
  Divider,
  Menu,
  MenuItem
} from "@mui/material";
import {
   useState
 } from 'react';
 import {
   Link,
   Outlet
 } from 'react-router-dom'
 import AdminMenu from "../../Json-api/AdminMenus";

const Admin = ()=>{

  const[active,setActive] = useState(true)
  const[width,setWidth] = useState(250);
  const[collapsible,setCollapsible] = useState(false);
  const[parent,setParent] = useState(null);
  const open = Boolean(parent);
  const openProfileMenu = (e)=>{
    const el = e.currentTarget;
    console.log(el)
    setParent(el);
  }

  const closeProfileMenu = ()=>{
    return setParent(null);
  }

  const controlDrawer = ()=>{
    return (
      setActive(!active),
      active ? setWidth(0) : setWidth(250)
    )
  }

  const Dropdown = ({data})=>{
    const dropdownDesign = (
      <>
        <Collapse in={collapsible} sx={{ml:4}}>
          {
            data.dropdownMenu.map((item)=>{
              return <Nav data={item}key={item.id} />
            })
          }
        </Collapse>
      </>
    );
    return dropdownDesign;
  }

  const Nav = ({data})=>{
    console.log(data)
      const navDesign = (
        <>
        <ListItem sx={{ py: 0 }}>
          <ListItemButton
            component= {Link}
            to= {data.link ? data.link : false}
            onClick={data.isDropdown ? ()=>setCollapsible(!collapsible) : null}>
            <ListItemIcon>
              <span className="material-icons-outlined">{data.icon}</span>
            </ListItemIcon>
            <ListItemText primary={data.label} />
            {
              data.isDropdown ? <span className="material-icons-outlined">expand_circle_down</span> : null
            }
          </ListItemButton>

        </ListItem>
        {
          data.isDropdown ? <Dropdown data={data}/> : null
        }
        </>
      );
      return navDesign;
  }

  const MenuList = ({adminMenu})=>{
    const menuDesign = (
      <>
        <List subheader={<ListSubheader>{adminMenu.cat}</ListSubheader>}>
          {
            adminMenu.menus.map((menu)=>{
              return <Nav data={menu} key={menu.id} />
            })
          }
        </List>
      </>
    );
    return menuDesign;
  }

  const design = (
    <>
      <Stack>
        <Drawer open={active} variant="persistent"
        sx={{
          width:width,
          "& .MuiDrawer-paper":{
            width: width
          }
        }}
        >
        {
          AdminMenu.map((admin)=>{
            return <MenuList adminMenu={admin} key={admin.id}/>
          })
        }
        </Drawer>
        <AppBar position="fixed"
          elevation= {0}
          sx={{
            width:`calc(100% - ${width}px)`,
            transition: "0.5s",
            background: "inherit",
            color: "inherit"
          }}
        >
        <Stack direction="row" justifyContent="space-between">
          <Toolbar>
            <IconButton sx={{color:"inherit"}} onClick={controlDrawer}>
            <span className="material-icons-outlined">menu</span>
            </IconButton>
          </Toolbar>
          <Toolbar>
          <Stack direction="row" justifyContent='center' alignItems="center" spacing="5px" style={{pr: 3}}>
            <IconButton sx={{color:"inherit"}}>
            <span className="material-icons-outlined">shopping_bag</span>
            </IconButton>
            <IconButton sx={{color:"inherit"}}>
            <span className="material-icons-outlined">shopping_cart</span>
            </IconButton>
            <IconButton sx={{color:"inherit"}}>
            <span className="material-icons-outlined">shopping_bag</span>
            </IconButton>
            <IconButton sx={{color:"inherit"}} onClick={openProfileMenu}>
            <Avatar src="https://mui.com/static/images/avatar/2.jpg" />
            </IconButton>
            <Menu
              anchorEl={parent}
              open={open}
              onClick={closeProfileMenu}
              onClose={closeProfileMenu}
              PaperProps={{
                   elevation: 0,
                   sx: {
                     overflow: 'visible',
                     filter: 'drop-shadow(0px 2px 8px rgba(0,0,0,0.32))',
                     mt: 1.5,
                     '& .MuiAvatar-root': {
                       width: 32,
                       height: 32,
                       ml: -0.5,
                       mr: 1,
                     },
                     '&:before': {
                       content: '""',
                       display: 'block',
                       position: 'absolute',
                       top: 0,
                       right: 14,
                       width: 10,
                       height: 10,
                       bgcolor: 'background.paper',
                       transform: 'translateY(-50%) rotate(45deg)',
                       zIndex: 0,
                     },
                   },
                 }}
                 transformOrigin={{ horizontal: 'right', vertical: 'top' }}
                 anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
              >
              <MenuItem>
                <Avatar>B</Avatar>
              </MenuItem>
              <Divider />
              <MenuItem>
                <span className="material-icons-outlined">shopping_bag</span>
                <span className="material-icons-outlined">shopping_bag</span>
                <span className="material-icons-outlined">shopping_bag</span>
              </MenuItem>
            </Menu>
          </Stack>
          </Toolbar>
        </Stack>
       </AppBar>
       <Divider />
       <Stack sx={{
         ml: `${width}px`,
         mt: 8,
         p: 2,
         background: "#f1f1f1",
         height: "100vh"
       }}>
        <Outlet />
       </Stack>
    </Stack>
    </>
  );
  return design;
}
export default Admin;
