import {
  createStore,
  applyMiddleware
} from "redux";

import logger from "redux-logger";
import thunk from "redux-thunk";

import SignupReducer from "./Signup/Signup.action";

const Storage = createStore((state,action)=>{
  return {
    message: "welcome"
  }
},{},applyMiddleware(logger,thunk));

export default Storage;
