import {
  Grid,
  Button,
  Container,
  TextField,
  Stack,
  FormGroup,
  FormControlLabel,
  Checkbox
} from "@mui/material"

import {
  LoadingButton
} from "@mui/lab";

import {
  useState,
  useEffect
} from "react";

import {
  ForgotRequest
} from "./Forgot.action";

import {
  useDispatch,
  useSelector
} from "react-redux";

const Forgot = ()=>{
  const dispatch = useDispatch();
  const {ForgotReducer} = useSelector(response=>response);
  useEffect(()=>{},[])
  const[verifyForm,setVerifyForm] = useState(false);

  const design = (
    <>
    <Container sx={{m:4}}>
      <Grid container>
        <Grid item xs={12} sm={6}>One</Grid>
        <Grid item xs={12} sm={6}>
         <h1>Forgot Password</h1>
         {
         !verifyForm ? <form onSubmit={(e)=>dispatch(ForgotRequest(e))}>
            <Stack direction="column">
            <TextField
                name="email"
                label="Email id"
                variant="outlined"
                sx={{my:3}}
            />
            <div>
            <LoadingButton variant="contained" type="submit">Forgot</LoadingButton>
            </div>
            </Stack>
         </form> :
         <form>
            <Stack direction="column">
            <TextField
                name="code"
                label="Verification code"
                variant="outlined"
                sx={{mt:3}}
                type="number"
            />
            <TextField
                name="password"
                label="New Password"
                variant="outlined"
                sx={{my:3}}
                type="password"
             />

            <div>
            <LoadingButton variant="contained" type="submit">Submit</LoadingButton>
            </div>
            </Stack>
         </form>
       }
        </Grid>
      </Grid>
    </Container>
    </>
  );
  return design;
}
export default Forgot;
