  const[data,setData] = useState([{
    name: 'aftab',
    roll: 1
  }])

  const NewTr = ({newItem})=>{
      <tr>
        <td>{newItem.name}</td>
        <td>{newItem.roll}</td>
      </tr>
  }

  const addMore = ()=>{
    alert()
  }

  const design = (
    <>
