import {
  EMAIL_SENDED,
  USER_NOT_FOUND,
  FORGOT_REQUEST,
  CHANGE_PASSWORD_REQUEST,
  PASSWORD_CHANGED,
  INVALID_CODE
} from "./Forgot.state";


const Model = {
  isLoading: false,
  userNotFound: false,
  success: false,
  passwordChanged: false,
  invalidCode: false
}

const ForgotReducer = (state=Model,action)=>{
  switch (action.type) {
    case FORGOT_REQUEST: return {
      ...state,
      isLoading: true,
      userNotFound: false,
      success: false
    }

    case EMAIL_SENDED: return {
      ...state,
      isLoading: false,
      userNotFound: false,
      success: true
    }

    case USER_NOT_FOUND: return {
      ...state,
      isLoading: false,
      userNotFound: true,
      success: false
    }
    default: return state;

  }
}
export default ForgotReducer
