import {
   useState,
   useEffect
 } from 'react';
import $ from 'jquery';
import {
  Button,
  Container,
  Card
} from "react-bootstrap";
 const Http = ()=>{
   const[data,setData] = useState([]);
   const fetchData = ()=>{
     $.ajax({
       type: 'GET',
       url: 'http://localhost:3232',
       success: function(response){
         return setData(response);
       }
     });
   }
   useEffect(()=>{
     fetchData();
   });

 const CardItem = (cardData)=>{
   const design = (
     <>
     <Card className="mb-3">
         <Card.Header>{cardData.newItem.title}
         <i className='fa fa-edit'></i>
         </Card.Header>
         <Card.Body>
           <p>{cardData.newItem.body}</p>
         </Card.Body>
       </Card>
     </>
   );
   return design;
 }

  const design = (
    <>
    <Container className="py-4">
      <div className='d-flex justify-content-between mb-4'>
      <div>
        <h2>Comments</h2>
      </div>
      <div>
        <Button variant='success'>New Comment</Button>
      </div>
    </div>
      {
        data.map((item)=>{
        return <CardItem newItem = {item} key={item.id}/>
      })
      }
    </Container>
    </>
  );
  return design;
}

export default Http;
