import {
   useState,
   useEffect
 } from 'react';
import $ from 'jquery';
import {
  Button,
  Container,
  Card
} from "react-bootstrap";
 const Http = ()=>{
   const[data,setData] = useState([]);
   const fetchData = ()=>{
     $.ajax({
       type: 'GET',
       url: 'http://localhost:3232',
       success: function(response){
         return setData(response);
       }
     });
   }
   useEffect(()=>{
     fetchData();
   });

   const demo = ()=>{
     console.log("hello");
   }

 const CardItem = (cardData)=>{
   const design = (
     <>
     <Card className="mb-3">
         <Card.Header className="d-flex justify-content-between">
         <div>
         <h5>{cardData.newItem.title}</h5>
         </div>
         <div style={{fontSize:"30px"}}>
         <i className="btn btn-info fa fa-edit mx-2" onClick={demo}></i>
         <i className="btn btn-danger fa fa-trash" onClick={demo()}></i>
         </div>
         </Card.Header>
         <Card.Body>
           <p>{cardData.newItem.body}</p>
         </Card.Body>
       </Card>
     </>
   );
   return design;
 }

  const design = (
    <>
    <Container className="py-4">
      <div className='d-flex justify-content-between mb-4'>
      <div>
        <h2>Comments</h2>
      </div>
      <div>
        <Button variant='success'>New Comment</Button>
      </div>
    </div>
      {
        data.map((item)=>{
        return <CardItem newItem = {item} key={item.id}/>
      })
      }
    </Container>
    </>
  );
  return design;
}

export default Http;
