import {
  LOGIN_REQUEST,
  LOGIN_SUCCESS,
  INCORRECT_PASSWORD,
  USER_NOT_FOUND
} from "./Login.state";

import axios from "axios";
axios.defaults.baseURL="http://localhost:3434";

const loginRequest = ({formData})=>{
  return async (dispatch)=>{
    try {
      const response = await axios({
        method: "post",
        url: "/login",
        data: formData
      })
    } catch (err) {
      console.log(err)
    }
  }
}

export {
  loginRequest
}
