import {
  SIGNUP_REQUEST,
  SIGNUP_SUCCESS,
  SIGNUP_ERROR
} from "./Signup.state";

//http hooks can not imported in action

import axios from "axios";

const SignupRequest = (formData)=>{
  return (dispatch)=>{
    const response = axios({
      method: "post",
      url: "http://localhost:3434/signup",
      data: formData
    });
    console.log(response);
    dispatch({
      type: SIGNUP_REQUEST,
      payload: formData
    })
  }
}

export {
  SignupRequest
}
