import axios from "axios";
axios.defaults.baseURL="http://localhost:3434";

const ForgotRequest = (e)=>{
  e.preventDefault();
  const email = e.target[0].value;
  return async (dispatch)=>{
    try {
        const response = await axios({
          method:"post",
          url:"/forgot-password",
          data: {
            email: email
          }
        })
        console.log(response)
    } catch (err) {
      console.log(err);
    }
  }
}

export {
  ForgotRequest
}
