import {
  FORGOT_REQUEST,
  EMAIL_SENDED,
  USER_NOT_FOUND
} from "./Forgot.state";
import axios from "axios";
axios.defaults.baseURL="http://localhost:3434";

const requestForget = (e)=>{
  e.preventDefault();
  const email = e.target[0].value;
  return async (dispatch)=>{
    try {
        dispatch({
          type: "FORGOT_REQUEST"
        })
        const response = await axios({
          method:"post",
          url:"/forgot-password",
          data: {
            email: email
          }
        })
        dispatch({
          type: "EMAIL_SENDED"
        })
    } catch (err) {
      dispatch({
        type: "USER_NOT_FOUND"
      })
    }
  }
}

const changePassword = (e,formData)=>{
  e.preventDefault();
  console.log(formData);
}
export {
  requestForget
}
