import axios from "axios";
axios.defaults.baseURL="http://localhost:3434";

const ForgotRequest = (e)=>{
  e.preventDefault();
  const email = e.target[0].value;
  return async (dispatch)=>{
    try {
        await axios({
          method:"post",
          url:"/forgot",
          data: {
            email: email
          }
        })
    } catch (e) {


    }
  }
}

export {
  ForgotRequest
}
