import {
  useEffect,
  useState
} from "react";
import $ from 'jquery';
const useHttp = (request)=>{
  const[response,setResponse] = useState([]);
  useEffect(()=>{
    $.ajax(request);
  },[request])
  request.success = (r)=>{
     setResponse(r);
  }
  return [response];
  // const design = ()=>{
  //
  // }
  // return design;
}

export default useHttp;
