import {
   useState,
   useEffect
 } from 'react';
import $ from 'jquery';
import {
  Button,
  Container,
  Card
} from "react-bootstrap";
 const Http = ()=>{
   const[data,setData] = useState([]);
   const[id,setId] = useState(0);
   const fetchDataById = ()=>{
     $.ajax({
       type: 'GET',
       url: `http://localhost:3232/${id}`,
       success: function(response){
         return setData([response]);
       }
     });
   }
   useEffect(()=>{
     fetchDataById();
   },[id]);

   const updateComment = (data)=>{
     console.log(data);
   }

   const deleteComment = (id)=>{
     return setId(id);
   }

 const CardItem = (cardData)=>{
   const design = (
     <>
     <Card className="mb-3">
         <Card.Header className="d-flex justify-content-between">
         <label>{cardData.newItem.title}</label>
         <div>
         <button className="btn border" style={{marginRight:'3px'}} onClick={()=>updateComment(cardData.newItem)}>
         <i className="fa fa-edit"></i>
         </button>
         <button className="btn border" onClick={()=>deleteComment(cardData.newItem.id)}>
         <i className="fa fa-trash"></i>
         </button>
         </div>
         </Card.Header>
         <Card.Body>
           <p>{cardData.newItem.body}</p>
         </Card.Body>
       </Card>
     </>
   );
   return design;
 }

  const design = (
    <>
    <Container className="py-4">
      <div className='d-flex justify-content-between mb-4'>
      <div>
        <h2>Comments</h2>
      </div>
      <div>
        <Button variant='success'>New Comment</Button>
      </div>
    </div>
      {
        data.map((item)=>{
        return <CardItem newItem = {item} key={item.id}/>
      })
      }
    </Container>
    </>
  );
  return design;
}

export default Http;
