import {
  SIGNUP_REQUEST,
  SIGNUP_SUCCESS,
  SIGNUP_ERROR
} from "./Signup.state";

//http hooks can not imported in action
import axios from "axios";
axios.defaults.baseURL = "http://localhost:3434";
const SignupRequest = (formData)=>{
  return async(dispatch)=>{
    const response = await axios({
      method: "post",
      url: "/signup",
      data: formData
    });
    console.log(response);
    dispatch({
      type: SIGNUP_REQUEST,
      payload: formData
    })
  }
}

export {
  SignupRequest
}
