import {
  Stack,
  Drawer,
  AppBar,
  Toolbar,
  IconButton,
  ListItemText,
  ListItemIcon,
  ListItemButton,
  ListItem,
  List
} from "@mui/material";
import {
  Menu
 } from '@mui/icons-material';

 import {
   useState
 } from 'react';

 import AdminMenu from "../../Json-api/AdminMenus";

const Admin = ()=>{

  const[active,setActive] = useState(true)
  const[width,setWidth] = useState(250);

  const controlDrawer = ()=>{
    return (
      setActive(!active),
      active ? setWidth(0) : setWidth(250)
    )
  }

  const Nav = ({menu})=>{
    <List>
        <ListItem disablePadding>
          <ListItemButton>
            <ListItemIcon>
              <span className="material-icons">{menu.label}</span>
            </ListItemIcon>
            <ListItemText primary="Inbox" />
          </ListItemButton>
        </ListItem>
    </List>
  }
  const design = (
    <>
      <Stack>
        <Drawer open={active} variant="persistent"
        sx={{
          width:width,
          "& .MuiDrawer-paper":{
            width: width,
            bgcolor: "#f5f5f5"
          }
        }}
        >
        {
          AdminMenu.map((menu)=>{
            return <Nav item={menu} key={menu.id}/>
          })
        }
        </Drawer>
        <AppBar position="fixed"
          sx={{
            width:`calc(100% - ${width}px)`,
            transition: "0.5s"
          }}
        >
          <Toolbar>
            <IconButton sx={{color:"inherit"}} onClick={controlDrawer}>
            <span className="material-icons">menu</span>
            </IconButton>
          </Toolbar>
       </AppBar>
       <Stack sx={{
         ml: `${width}px`,
         mt: 5,
         p: 2
       }}>
        <h1>Testing</h1>
       </Stack>
    </Stack>
    </>
  );
  return design;
}
export default Admin;
