import {
  Stack,
  Drawer,
  AppBar,
  Toolbar,
  IconButton,
  ListItemText,
  ListItemIcon,
  ListItemButton,
  ListItem,
  List,
  ListSubheader
} from "@mui/material";

 import {
   useState
 } from 'react';

 import AdminMenu from "../../Json-api/AdminMenus";

const Admin = ()=>{

  const[active,setActive] = useState(true)
  const[width,setWidth] = useState(250);

  const controlDrawer = ()=>{
    return (
      setActive(!active),
      active ? setWidth(0) : setWidth(250)
    )
  }

  const Nav = ({data})=>{
      const navDesign = (
        <ListItem
          sx={{
            py: 0
          }}
          >
          <ListItemButton>
            <ListItemIcon>
              <span className="material-icons">{data.icon}</span>
            </ListItemIcon>
            <ListItemText primary={data.label} />
          </ListItemButton>
        </ListItem>
      );
      return navDesign;
  }

  const MenuList = ({menus})=>{
    const menuDesign = (
      <>
        <List subheader={<ListSubheader>{menus.cat}</ListSubheader>}>
          {
            menus.map((menu)=>{
              return <Nav data={menu} key={menu.id} />
            })
          }
        </List>
      </>
    );
    return menuDesign;
  }

  const design = (
    <>
      <Stack>
        <Drawer open={active} variant="persistent"
        sx={{
          width:width,
          "& .MuiDrawer-paper":{
            width: width
          }
        }}
        >
        {
          AdminMenu.map((admin)=>{
            return <MenuList menus={admin} key={admin.id}/>
          })
        }
        </Drawer>
        <AppBar position="fixed"
          sx={{
            width:`calc(100% - ${width}px)`,
            transition: "0.5s"
          }}
        >
          <Toolbar>
            <IconButton sx={{color:"inherit"}} onClick={controlDrawer}>
            <span className="material-icons">menu</span>
            </IconButton>
          </Toolbar>
       </AppBar>
       <Stack sx={{
         ml: `${width}px`,
         mt: 5,
         p: 2
       }}>
        <h1>Testing</h1>
       </Stack>
    </Stack>
    </>
  );
  return design;
}
export default Admin;
