import {
   useState,
   useEffect
 } from 'react';
import $ from 'jquery';
import {
  Button,
  Container,
  Card,
  Modal,
} from "react-bootstrap";
 const Http = ()=>{
   const[data,setData] = useState([]);
   const[openModal,setModal] = useState(false);
   const[counter,setCounter] = useState(0);
   const[totalComment,setTotalComment] = useState(0);
   const[animation,setAnimation] = useState("animate__animated animate__bounce");
   const fetchDataById = ()=>{
     $.ajax({
       type: 'GET',
       url: `http://localhost:3232/${counter}`,
       success: function(response){
         // console.log(response);
         return (
           setData([response]),
           setTotalComment(response.totalComment)
         )
       }
     });
   }
   useEffect(()=>{
     fetchDataById();
   },[counter]);

   const insertComment = ()=>{
     alert()
   }
   const updateComment = (data)=>{
     console.log(data);
   }

   const deleteComment = (id)=>{
     alert(id);
   }

   const next = ()=>{
      return(
        setCounter(counter+1),
        setAnimation("animate__animated animate__slideInLeft")
      )
   }

   const prev = ()=>{
     return(
       setCounter(counter-1),
       setAnimation("animate__animated animate__slideInRight")
     )
   }

 const CardItem = (cardData)=>{
   const design = (
     <>
     <Card className={"mb-3 "+animation}>
         <Card.Header className="d-flex justify-content-between">
         <label>{cardData.newItem.title}</label>
         <div>
         <button className="btn border" style={{marginRight:'3px'}} onClick={()=>updateComment(cardData.newItem)}>
         <i className="fa fa-edit"></i>
         </button>
         <button className="btn border" onClick={()=>deleteComment(cardData.newItem.id)}>
         <i className="fa fa-trash"></i>
         </button>
         </div>
         </Card.Header>
         <Card.Body>
           <p>{cardData.newItem.body}</p>
         </Card.Body>
       </Card>
     </>
   );
   return design;
 }

  const design = (
    <>
    <Container className="py-4">
      <div className='d-flex justify-content-between mb-4'>
      <div>
        <h2>Comments-{counter} / {totalComment}</h2>
      </div>
      <div>
        <Button variant='success' onClick={()=>setModal(true)}>New Comment</Button>
      </div>
    </div>
      {
        data.map((item)=>{
        return <CardItem newItem = {item} key={item.id}/>
      })
      }

      <Modal show={openModal} onHide={()=>setModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Header</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <form onSubmit={insertComment}>
          <label>Title</label><br />
          <input type="text" className='form-control'/>
          <br />
          <label>Description </label><br />
          <textarea className='form-control'></textarea><br />
          <Button variant="primary">Submit</Button>
        </form>
        </Modal.Body>
     </Modal>
     <div className='d-flex justify-content-end'>
      <Button variant="info" style={{marginRight:"2px"}} onClick={counter > 0 ? prev : null}>Prev</Button>
      <Button variant="success" onClick={next}>Next</Button>
      </div>
    </Container>
    </>
  );
  return design;
}

export default Http;
