import {
  SIGNUP_REQUEST
} from "./Signup.state";

const Model = {
  isLoading: false,
  data: null,
  error: null
}
const SignupReducer = (state=Model,action)=>{
  if(action.type === SIGNUP_REQUEST){
    return {
      ...state,
      data: action.payload
    }
  }
}

export default SignupReducer;
